from typing import Optional, Callable, Awaitable
from pydantic import BaseModel, Field
import requests

class Pipe:
    class Valves(BaseModel):
        server_url: str = Field(
            default="http://localhost:3001",
            description="URL of the intelligent-rag-server"
        )
        project_id: str = Field(
            default="the_great_fire",
            description="Project ID to query against"
        )
        thinking_depth: int = Field(
            default=7,
            description="Depth of thinking (1-10)",
            ge=1,
            le=10
        )

    def __init__(self):
        self.type = "pipe"
        self.id = "intelligent_rag"
        self.name = "Intelligent RAG"
        self.valves = self.Valves()

    async def pipe(
        self,
        body: dict,
        __user__: Optional[dict] = None,
        __event_emitter__: Optional[Callable[[dict], Awaitable[None]]] = None,
        __event_call__: Optional[Callable[[dict], Awaitable[dict]]] = None,
    ) -> Optional[dict]:
        messages = body.get("messages", [])

        if messages:
            question = messages[-1]["content"]
            try:
                headers = {"Content-Type": "application/json"}
                payload = {
                    "projectId": self.valves.project_id,
                    "query": question,
                    "thinkingDepth": self.valves.thinking_depth
                }
                response = requests.post(
                    f"{self.valves.server_url}/api/query",
                    json=payload,
                    headers=headers
                )

                if response.status_code == 200:
                    rag_response = response.json()
                    answer = rag_response.get("content", "")

                    # Add the answer to the messages
                    body["messages"].append({
                        "role": "assistant",
                        "content": answer
                    })

                    # Return None to indicate success
                    return None

                else:
                    raise Exception(f"Error: {response.status_code} - {response.text}")

            except Exception as e:
                body["messages"].append({
                    "role": "assistant",
                    "content": f"Error: {str(e)}"
                })
                return None
        else:
            body["messages"].append({
                "role": "assistant",
                "content": "No messages found in the request body"
            })
            return None
